INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (1, null, '2019-02-07 14:40:07', null, 0, 'CONSULTAS', '¿Cómo puedo contactarme con ustedes ante cualquier duda?', 'Usted puede contactarse con nosotros desde la funcionalidad "Consultas".');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (2, null, '2019-02-07 14:40:07', null, 0, 'CONSULTAS', '¿Cuál es el horario de atención?', 'Nuestro horario de atención es de lunes a viernes de 08 hs a 20 hs y los sábados de 09 hs a 19 hs.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (3, null, '2019-02-07 14:40:07', null, 0, 'COORDINACION', '¿Cómo hago para saber si mi Obra Social/Prepaga les asignó mi pedido?', 'Para saber si su pedido nos fue asignado, ingrese a la funcionalidad "Pedidos" donde encontrará sus pedidos en curso e históricos.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (4, null, '2019-02-07 14:40:07', null, 0, 'COORDINACION', '¿Cómo hago para saber si ya puedo coordinar mi pedido?', 'Ingresando a la funcionalidad "Pedidos" usted podrá coordinar su pedido cuando el estado del mismo es "Para Coordinar".');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (5, null, '2019-02-07 14:40:07', null, 0, 'COORDINACION', '¿Qué puedo hacer si las opciones propuestas para coordinar mi pedido no son de mi preferencia?', 'Si ninguna de las opciones son de su preferencia, usted podrá rechazar la propuesta de coordinación del pedido y comentarnos el motivo de su rechazo.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (6, null, '2019-02-07 14:40:07', null, 0, 'ENTREGA', '¿Cuánto tiempo permanecerá mi pedido en la Farmacia?', 'Su pedido permanecerá en la farmacia por 7 días.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (7, null, '2019-02-07 14:40:07', null, 0, 'ENTREGA', '¿Mi pedido puede ser entregado a otra persona?', 'Su pedido puede ser entregado a una persona que sea mayor de edad  y cuente con la receta ORIGINAL y la credencial del destinatario de la medicación.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (8, null, '2019-02-07 14:40:07', null, 0, 'ENTREGA', '¿Cómo realizo el seguimiento de un pedido en curso?', 'Para hacer el seguimiento de su pedido consulte ingresando a la funcionalidad “Pedidos” donde le indicaremos el estado en el que se encuentra el mismo.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (9, null, '2019-02-07 14:40:07', null, 0, 'ENTREGA', '¿Cómo hago para cancelar mi pedido? ', 'Para cancelar un pedido ya coordinado, contáctese con nosotros a través de la funcionalidad "Consultas".');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (10, null, '2019-02-07 14:40:07', null, 0, 'PRODUCTO', '¿Cómo puedo darme cuenta si alguno de los productos requiere cadena de frío?', 'Los productos que requieren cadena de frío están discriminados en el Remito con una leyenda que indica su condición de conservación. Además, encontrará dentro del empaque un instructivo con mayor detalle.');
INSERT INTO scienza.pregunta_frecuente (id, fecha_delete, fecha_insert, fecha_update, version, categoria, pregunta, respuesta) VALUES (11, null, '2019-02-07 14:40:07', null, 0, 'MEDIOS DE PAGO', '¿Cuáles son los medios de pago disponibles?', 'Aceptamos tarjetas de crédito Visa, MasterCard, transferencias y depósitos bancarios.');